sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		var rn = GlideDateTime();
		var givenDate = GlideDateTime(strDate);
		return givenDate.before(rn);
	},

	isDateToday: function(strDate) {
		var rn = GlideDateTime();
		var givenDate = GlideDateTime(strDate);
		return ((rn.getLocalDate()).compareTo(givenDate.getLocalDate()) == 0);
	},
    type: 'NeedItUtils'
};
